import styled from "styled-components";

var { mycolor } = "#c6538c";

export const SpinnerOverlay = styled.div`
  height: 60vh;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const SpinnerContainer = styled.div`
  display: inline-block;
  width: 80px;
  height: 80px;
  border: 5px solid rgba(25, 190, 180, 0.5);
  border-radius: 50%;
  border-right-color: grey;
  animation: spin 2s ease-in-out infinite;
  -webkit-animation: spin 1s ease-in-out infinite;
  @keyframes spin {
    to {
      -webkit-transform: rotate(720deg);
    }
  }
  @-webkit-keyframes spin {
    to {
      -webkit-transform: rotate(720deg);
    }
  }
`;

export const SpinnerContainer2 = styled.div`
  transform: rotateZ(45deg);
  perspective: 1000px;
  border-radius: 50%;
  &:before,
  &:after {
    content: "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: inherit;
    height: inherit;
    border-radius: 50%;
    animation: 1s spin linear infinite;
  }
  &:before {
    transform: rotateX(70deg);
  }
  &:after {
    transform: rotateY(70deg);
    animation-delay: 0.4s;
  }

  @keyframes rotate {
    0% {
      transform: translate(-50%, -50%) rotateZ(0deg);
    }
    100% {
      transform: translate(-50%, -50%) rotateZ(360deg);
    }
  }

  @keyframes rotateccw {
    0% {
      transform: translate(-50%, -50%) rotate(0deg);
    }
    100% {
      transform: translate(-50%, -50%) rotate(-360deg);
    }
  }

  @keyframes spin {
    0%,
    100% {
      box-shadow: 0.2em 0px 0 0px ${mycolor};
    }
    12% {
      box-shadow: 0.2em 0.2em 0 0 ${mycolor};
    }
    25% {
      box-shadow: 0 0.2em 0 0px ${mycolor};
    }
    37% {
      box-shadow: -0.2em 0.2em 0 0 ${mycolor};
    }
    50% {
      box-shadow: -0.2em 0 0 0 ${mycolor};
    }
    62% {
      box-shadow: -0.2em -0.2em 0 0 ${mycolor};
    }
    75% {
      box-shadow: 0px -0.2em 0 0 ${mycolor};
    }
    87% {
      box-shadow: 0.2em -0.2em 0 0 ${mycolor};
    }
  }
`;
