import ShopActionTypes from "./shop.types";

const INITIAL_STATE = {
  collections: null,
  isFetching: false,
  errMessage: undefined,
};
// since the initial state collections is null, make sure to update shop.selectors (ternary operator)
// also add the spinner, because data fetching is async, might be an error when data hasn't been retrieved yet

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ShopActionTypes.FETCH_COLLECTIONS_START:
      return {
        ...state,
        isFetch: true,
      };

    case ShopActionTypes.FETCH_COLLECTIONS_SUCCESS:
      return {
        ...state,
        isFetching: false,
        collections: action.payload,
      };
    case ShopActionTypes.FETCH_COLLECTIONS_FAIL:
      return {
        ...state,
        isFetching: false,
        errMessage: action.payload,
      };
    default:
      return state;
  }
};

export default shopReducer;
